import React, { useState } from 'react';
import axios from 'axios';
import { Link } from '@reach/router';


function Starwars() {
    const [starwars, setStarwars] = useState({})
    const [formState, setFormState] = useState({
        category: "people",
        id: ""
    })
    function getStarwars(e) {
        e.preventDefault();
        axios.get('https://swapi.co/api/' + formState.category + '/' + formState.id + "/")
            .then(response => {
                console.log(response)
                setStarwars({ ...response.data })
            })
            .catch(err => console.log(err))
    }

    function onChange(e) {
        setFormState({
            ...formState,
            [e.target.name]: e.target.value
        })
    }

    console.log("starwars results are: ", starwars);

    return (
        <div>
            <h1>Search for: </h1>
            <form onSubmit={getStarwars}>
                <select name="category" onChange={onChange}>
                    <option value="people">People</option>
                    <option value="planets">Planets</option>
                    <option value="films">Films</option>
                    <option value="species">Species</option>
                    <option value="vehicles">Vehicles</option>
                    <option value="starships">Starships</option>
                </select>
                <input onChange={onChange} type="text" placeholder="ID" />
                <button type='submit'>Search</button>
                <p >{starwars.name}</p>
            </form>
        </div>
    )
}

export default Starwars
