import React, { useState } from 'react';
import axios from 'axios';
import { Link } from '@reach/router';


function Starwars() {
    const [starwars, setStarwars] = useState([])

    function getStarwars() {
        axios.get('https://swapi.co/api/' +st)
        .then(response => setStarwars([...response.data.results]))
        .catch(err => console.log(err))
    }

    function onChange(event) {
        setStarwars({
            ...starwars[event.target.value]
        })
    }

    console.log("starwars results are: ",starwars);

    return (
        <div>
            <h1>Search for: </h1>
            <form >
            <select>
                <option onChange={onChange} value="people" >People</option>
                <option onChange={onChange} value="planets">Planets</option>
                <option onChange={onChange} value="films">Films</option>
                <option onChange={onChange} value="species">Species</option>
                <option onChange={onChange} value="vehicles">Vehicles</option>
                <option onChange={onChange} value="starships">Starships</option>
            </select>
            <button onClick={getStarwars}>Search</button>
            {starwars.map((el,i) =>
            <p key={i}>{el.name}, {el.height}, {el.birth_year}</p>
            )}
            </form>
        </div>
    )
}

export default Starwars
